import React from "react";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import "../App.css";
import { Divider } from "@mui/material";
import { Rating } from "@mui/material";

function Product() {
  const location = useLocation();
  const { product } = location.state;

  return (
      <>
      <div className="heading">
        <h2>Product Details</h2>
      </div>
      <Divider />
    <div className="product">
      <div className="product-image-container">
        <img className="product-image" src={product.image}></img>
      </div>
      <div>
        <Card sx={{ width: 600,  }} className="product-details">
          <CardContent>
            <Typography variant="h4" component="div">
              {product.title}
            </Typography>
            <Typography
              sx={{ fontSize: 18 }}
              color="text.secondary"
              gutterBottom
            >
              {product.description}
            </Typography>
            <Typography sx={{ mb: 1.9 }} color="text.secondary">
              Category: {product.category}
            </Typography>
            <Typography style={{fontSize: 28, }}>
              Price: ${product.price}
              <br />
              Rating: {product.rating.rate}
              <Rating name="simple-controlled" value={product.rating.rate} style={{marginLeft: 10}}/>
            </Typography>
          </CardContent>
          <CardActions>
          <Button size="large" style={{ fontWeight: "bolder" }}>
                    Add To Cart
                  </Button>
          </CardActions>
        </Card>
      </div>
    </div>
    </>
  );
}

export default Product;
