import React from "react";
import { Divider } from "@mui/material";
import Footer from "./Footer";
import { useSelector } from "react-redux";
import "../App.css";
import { useDispatch } from "react-redux";
import { addProduct, deleteProduct } from "../redux/actions";
import { Button } from "@mui/material";

function Cart() {
  const state = useSelector((state) => state.cart);

  const dispatch = useDispatch();

  const addQty = (product) => {
    dispatch(addProduct(product));
  };

  const delQty = (product) => {
    dispatch(deleteProduct(product));
  };

  return (
    <div className="cart">
      <div className="heading">
        <h2>Cart</h2>
      </div>
      <Divider />

      <div className="cart-products">
        {state.length === 0 ? (
          <div className="cart-details">
            <h4>Your cart is empty </h4>
          </div>
        ) : (
          state.map((product) => {
            const { id, title, price, image, qty } = product;
            const totalPrice = qty * price;

            return (
              <>
               <Button variant="outlined" style={{
                        fontWeight: "bolder",
                        color: "white",
                        borderColor: "white",
                        height: 50,
                        backgroundColor: 'black'
                      }}>Checkout</Button>

<div className="cart-product" key={id}>
                <div className="cart-product-image">
                  <img src={image} className="cart-product-img"></img>
                </div>

                <div className="cart-product-details">
                  <div className="cart-product-title">{title}</div>
                  <div className="qty">
                    {qty} x ${price} = ${totalPrice}
                  </div>
                  <div className="incAndDec">
                    <Button
                      variant="outlined"
                      style={{
                        fontWeight: "bolder",
                        color: "black",
                        borderColor: "black",
                        fontSize: 30,
                        height: 50,
                      }}
                      onClick={() => addQty(product)}
                    >
                      +
                    </Button>
                    <Button
                      variant="outlined"
                      style={{
                        fontWeight: "bolder",
                        color: "black",
                        borderColor: "black",
                        fontSize: 30,
                        height: 50,
                      }}
                      onClick={() => delQty(product)}
                    >
                      -
                    </Button>
                  </div>
                </div>
              </div>
                      </>

            );
          })
        )}
      </div>
      <Footer />
    </div>
  );
}

export default Cart;
