import { Divider } from "@mui/material";
import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { Button } from "@mui/material";
import Footer from "./Footer";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { Navigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import { CircularProgress, Box } from "@mui/material";

import "react-toastify/dist/ReactToastify.css";

function Checkout() {
  const state = useSelector((state) => state.cart);
  const [totalPrice, setTotalPrice] = useState(
    JSON.parse(localStorage.getItem("total"))
  );
  const [loading, setLoading] = useState(false);

  const [product] = useState({
    name: "E-Shop",
    price: Math.round(totalPrice),
    description: "This is a sample",
  });

  toast.configure();
  async function handleToken(token, addresses) {
    const response = await axios.post("http://localhost:8080/checkout", {
      token,
      product,
    });
    if (response.status === 200) {
      toast("Success payment", { type: "success" });
      localStorage.removeItem("reduxState");
      localStorage.removeItem("total");
      setLoading(true);
      setTimeout(() => {
        window.location = "http://localhost:3000/products";
        setLoading(false);
      }, 10000);
    } else {
      toast("Failure payment", { type: "error" });
    }
    console.log(response.status);
  }

  useEffect(() => {
    setTotalPrice(JSON.parse(localStorage.getItem("total")));
    console.log(totalPrice);
  }, []);

  return (
    loading ?  <Box sx={{ display: "flex" }} style={{display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "inherit",
      marginTop: 300
      }}>
    <CircularProgress />
  </Box> 
  : 
    <div className="checkout-container">
      <div className="heading cart-heading">
        <h2>Checkout</h2>
        <h2>Total Price: &nbsp; ${Math.round(totalPrice)}</h2>
        <StripeCheckout
          stripeKey="pk_test_51KcdwcLGzlwg0551cXUgVMhQCBD4XcEwzy1QajT7yeEinjli0i1mzCE6x9VvSxkFx1wINtcbjFdBrDiT5Ct1RRiq00NJigiTYs"
          token={handleToken}
          amount={product.price * 100}
          name={product.name}
          billingAddress
          shippingAddress
          style={{width: 200, height: 100, backgroundColor: 'red'}}
        />
      </div>
      <Divider />

        <div className="checkout">
          <div className="checkout-headings">
            <h3 className="checkout-heading">Items</h3>
            <h3 className="checkout-heading">Quantity</h3>
            <h3 className="checkout-heading">Price</h3>
          </div>
          <Divider />
          <div className="checkout-items">
            <div className="checkout-item">
              {state.map((product) => {
                const { id, image, qty, price } = product;
                return (
                  <div key={id}>
                    <div className="checkout-item-container">
                      <img
                        className="checkout-item-img"
                        src={image}
                        alt={"Product"}
                      ></img>
                      <h3>{qty}</h3>
                      <h3>${price * qty}</h3>
                    </div>
                    <Divider />
                  </div>
                );
              })}
            </div>
          </div>
          <Divider />
          <div className="total">
            <h1>Total Price: </h1>
            <h2>$&nbsp;{Math.round(totalPrice)}</h2>
          </div>
        </div>
      )
      <Footer />
    </div>
  );
}

export default Checkout;
