import React, { useState, useEffect } from 'react'
import image from '../assets/ecommerce-gd190ac368_1920.jpg';
import '../App.css';
import Products from './Products';
import FilterProduct from './FilterProduct';

function Home() {
    const [data, setData] = useState([]);
    const [newData, setNewData] = useState(data);
    const [loading, setLoading] = useState(false);

    const apiCall = async () => {
        try {
            const getData = await fetch('https://fakestoreapi.com/products');
            const res = await getData.json();
            console.log(res)
            setData(res);
            setLoading(false)
            setNewData(res)
        } catch (e) {
            console.log(e)
        }
    }

    function filter(cat) {
        const filter = data.filter((prod) => prod.category === cat)
        setNewData(filter)
    }

    useEffect(() => {
        setLoading(true);
        apiCall()
    }, [])

    return (
        <>
            <div className='image'>
                <img src={image} className='home-image'></img>
            </div>
            <Products
                data={data}
                setData={setData}
                newData={newData}
                setNewData={setNewData}
                loading={loading}
                setLoading={setLoading}
                filter={filter}
                apiCall={apiCall} />
        </>
    )
}

export default Home
