import React, { useState, useEffect } from 'react'
import Divider from '@mui/material/Divider';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import '../App.css';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Skeleton from '@mui/material/Skeleton';
import Rating from '@mui/material/Rating';


function Products() {

    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [value, setValue] = useState(4.2);

    useEffect(() => {
        setLoading(true);
        const apiCall = async () => {
            try {
                const getData = await fetch('https://fakestoreapi.com/products');
                const res = await getData.json();
                console.log(res)
                setData(res);
                setLoading(false)
            } catch (e) {
                console.log(e)
            }
        }
        apiCall()
    }, [])

    return (
        <div className='products'>
            <div className='heading'>
                <h2>All Products</h2>
            </div>
            <Divider />
            {loading
                ? <div className='skeleton'>
                    <Skeleton variant="rectangular" width={410} height={418} />
                    <Skeleton variant="rectangular" width={410} height={418} />
                    <Skeleton variant="rectangular" width={410} height={418} />
                </div>
                : <div className='all-products'>
                    {data.map((product) => {
                        const { id, title, price, description, category, image } = product;
                        const { rate } = product.rating;
                        return (
                            <Card sx={{ maxWidth: 360, }} key={id}>
                                <CardMedia
                                    component="img"
                                    image={image}
                                    height='400'
                                    alt="green iguana"
                                />
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="div">
                                        {title}
                                    </Typography>
                                    <h6>Category: <span style={{ fontWeight: 'normal' }}>{category}</span></h6>
                                    <h3>${price}</h3>
                                    <h6>Rating: <span style={{ fontWeight: 'normal' }}>{rate}</span>
                                        <Rating
                                            name="simple-controlled"
                                            value={value}
                                            onChange={(event, newValue) => {
                                                setValue(newValue);
                                            }}
                                        />
                                    </h6>
                                </CardContent>
                                <CardActions>
                                    <Button size="small">Add to Cart</Button>
                                </CardActions>
                            </Card>
                        )
                    })}
                </div>}

        </div>
    )
}

export default Products
