import React, { useState, useEffect } from 'react'
import Divider from '@mui/material/Divider';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import '../App.css';
import Skeleton from '@mui/material/Skeleton';
import Rating from '@mui/material/Rating';
import FilterProduct from './FilterProduct';


function Products() {

    const [data, setData] = useState([]);
    const [newData, setNewData] = useState(data);
    const [loading, setLoading] = useState(false);

    function filter(cat){
        data.forEach((product, i) => {
            const {category} = product;
            if(category === cat){
               const filter =  data.filter((prod) => prod.category === cat)
               setNewData(filter)
            }
        })
    }

    useEffect(() => {
        setLoading(true);
        const apiCall = async () => {
            try {
                const getData = await fetch('https://fakestoreapi.com/products');
                const res = await getData.json();
                console.log(res)
                setData(res);
                setLoading(false)
                setNewData(res)
            } catch (e) {
                console.log(e)
            }
        }
        apiCall()
    }, [])

    return (
        <div className='products'>
            <FilterProduct data={data} loading={loading} filter={filter}/>
            <div className='heading'>
                <h2>Featured Products</h2>
            </div>
            <Divider />
            {loading
                ? <div className='skeleton'>
                    <Skeleton variant="rectangular" width={410} height={418} animation="wave"/>
                    <Skeleton variant="rectangular" width={410} height={418} animation="wave"/>
                    <Skeleton variant="rectangular" width={410} height={418} animation="wave"/>
                    <Skeleton variant="rectangular" width={410} height={418} animation="wave"/>
                </div>
                : <div className='all-products'>
                    {newData.map((product) => {
                        const { id, title, price, description, category, image } = product;
                        const { rate } = product.rating;
                        return (
                            <Card sx={{ maxWidth: 360, }} key={id}>
                                <CardMedia
                                    component="img"
                                    image={image}
                                    height='380'
                                    alt="green iguana"
                                />
                                <CardContent>
                                    <Typography gutterBottom variant="h5" component="div">
                                        {title}
                                    </Typography>
                                    <h6>Category: <span style={{ fontWeight: 'normal' }}>{category}</span></h6>
                                    <h3>${price}</h3>
                                    <div className='rating'>
                                        <span>Rating: </span>
                                        <span style={{ fontWeight: 'normal' }}>{rate}</span>
                                            <Rating
                                                name="simple-controlled"
                                                value={rate}
                                            />
                                    </div>
                                </CardContent>
                                <CardActions>
                                    <Button size="small">Add to Cart</Button>
                                </CardActions>
                            </Card>
                        )
                    })}
                </div>}

        </div>
    )
}

export default Products
