import React, { useState, useEffect } from "react";
import Divider from "@mui/material/Divider";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import "../App.css";
import Skeleton from "@mui/material/Skeleton";
import Rating from "@mui/material/Rating";
import FilterProduct from "./FilterProduct";
import { Link } from "react-router-dom";

function Products() {
  const [data, setData] = useState([]);
  const [newData, setNewData] = useState(data);
  const [loading, setLoading] = useState(false);

  const apiCall = async () => {
    try {
      const getData = await fetch("http://localhost:8080/products");
      const res = await getData.json();
      console.log(res);
      setData(res);
      setLoading(false);
      setNewData(res);
    } catch (e) {
      console.log(e);
    }
  };

  function filter(cat) {
    const filter = data.filter((prod) => prod.category === cat);
    setNewData(filter);
  }

  useEffect(() => {
    setLoading(true);
    apiCall();
  }, []);

  return (
    <div className="products">
      <FilterProduct filter={filter} apiCall={apiCall} />
      <div className="heading">
        <h2>Featured Products</h2>
      </div>
      <Divider />
      {loading ? (
        <div className="skeleton">
          <Skeleton
            variant="rectangular"
            width={410}
            height={418}
            animation="wave"
          />
          <Skeleton
            variant="rectangular"
            width={410}
            height={418}
            animation="wave"
          />
          <Skeleton
            variant="rectangular"
            width={410}
            height={418}
            animation="wave"
          />
          <Skeleton
            variant="rectangular"
            width={410}
            height={418}
            animation="wave"
          />
        </div>
      ) : (
        <div className="all-products">
          {newData.map((product) => {
            const { id, title, price, category, image } = product;
            const { rate } = product.rating;
            return (
              <Card sx={{ maxWidth: 360 }} key={id} className="product-card">
                <CardMedia
                  component="img"
                  image={image}
                  height="380"
                  alt="green iguana"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {title}
                  </Typography>
                  <h6>
                    Category:{" "}
                    <span style={{ fontWeight: "normal" }}>{category}</span>
                  </h6>
                  <h3>${price}</h3>
                  <div className="rating">
                    <span>Rating: </span>
                    <span style={{ fontWeight: "normal" }}>{rate}</span>
                    <Rating name="simple-controlled" value={rate} />
                  </div>
                </CardContent>
                <CardActions className="product-buttons">
                  <Link
                    className="nav-link active"
                    to={`product/${id}`}
                    state={{ product }}
                  >
                    <Button size="large" style={{ fontWeight: "bolder" }}>
                      Buy Now
                    </Button>
                  </Link>
                  <Link to="/cart">
                    <Button size="large" style={{ fontWeight: "bolder" }}>
                      Go to Cart
                    </Button>
                  </Link>
                </CardActions>
              </Card>
            );
          })}
        </div>
      )}
      <div style={{marginBottom: 200}}>

    <h1>Create New Book</h1>


    <form action="http://localhost:8080/products" method="POST">
        <div className="form-group">
            <label htmlFor='ISBN'>ISBN</label>
            <input className="form-control" name="id"></input>
        </div>

        <div className="form-group">
            <label htmlFor='Title'>Title</label>
            <input className="form-control" name="title"></input>
        </div>

        <div className="form-group">
            <label htmlFor='Price'>Price</label>
            <input className="form-control" name="price" type='number'></input>
        </div>

        <div className="form-group">
            <label htmlFor='Description'>Description</label>
            <input className="form-control" name="description"></input>
        </div>
        <div className="form-group">
            <label htmlFor='Category'>Category</label>
            <input className="form-control" name="category"></input>
        </div>

        <div className="form-group">
            <label htmlFor='Rating'>Rating</label>
            <input className="form-control" name="rating"></input>
            <input className="form-control" name="rating"></input>
        </div>

        <button type="submit" className="btn btn-primary">Submit</button>
    </form>
</div>
    </div>
  );
}

export default Products;
