import React from "react";
import { Divider } from "@mui/material";
import Footer from "./Footer";
import { useSelector } from "react-redux";
import "../App.css";
import { useDispatch } from "react-redux";
import { addProduct, deleteProduct } from "../redux/actions";
import { Button } from "@mui/material";
import { Link, useLocation } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";

function Cart() {
  const state = useSelector((state) => state.cart);
  const [cartProductsPrice, setCartProductsPrice] = useState(
    state.map((prod) => prod.price)
  );
  const location = useLocation();

  const [totalPrice, setTotalPrice] = useState(0);

  const [prodPrice, setProdPrice] = useState();

  const dispatch = useDispatch();

  const addQty = (product) => {
    dispatch(addProduct(product));
    setTotalPrice(totalPrice + product.price);
    console.log(totalPrice);
    localStorage.setItem("total", JSON.stringify(totalPrice + product.price));
  };

  const delQty = (product) => {
    dispatch(deleteProduct(product));
    setTotalPrice(totalPrice - product.price);
    console.log(totalPrice);
    localStorage.setItem("total", JSON.stringify(totalPrice - product.price));
  };

  const sumCartArray = (arr) => {
    let sum = 0;
    for (let i = 0; i < arr.length; i++) {
      sum += arr[i];
    }
    setTotalPrice(sum);
    return sum;
  };

  useEffect(() => {
    let checkout = sumCartArray(cartProductsPrice);
    setTotalPrice(JSON.parse(localStorage.getItem("total")));
    let sum = 0;

    console.log(cartProductsPrice);

    console.log(totalPrice);

    // console.log(cartProductsPrice)
  }, []);

  return (
    <div className="cart">
      <div className="heading cart-heading">
        <h2>Cart</h2>
        <h2>Total Items: {state.length} </h2>
        <Link to="/checkout" state={{ totalPrice }}>
          <Button
            className="checkout-button"
            variant="outlined"
            style={{
              fontWeight: "bolder",
              color: "white",
              borderColor: "white",
              fontSize: 20,
              height: 50,
              backgroundColor: "#212529",
              borderRadius: 10,
            }}
          >
            Checkout
          </Button>
        </Link>
      </div>
      <Divider />

      <div className="cart-products">
        {state?.length === 0 ? (
          <div className="cart-details">
            <h4>Your cart is empty </h4>
          </div>
        ) : (
          state?.map((product) => {
            const { id, title, price, image, qty } = product;
            const totalPrice = qty * price;

            return (
              <div className="cart-product" key={id}>
                <div className="cart-product-image">
                  <img
                    src={image}
                    className="cart-product-img"
                    alt={"Product"}
                  ></img>
                </div>

                <div className="cart-product-details">
                  <div className="cart-product-title">{title}</div>
                  <div className="qty">
                    {qty} x ${price} = ${Math.round(totalPrice)}
                  </div>
                  <div className="incAndDec">
                    <Button
                      variant="outlined"
                      style={{
                        fontWeight: "bolder",
                        color: "black",
                        borderColor: "black",
                        fontSize: 30,
                        height: 50,
                      }}
                      onClick={() => addQty(product)}
                    >
                      +
                    </Button>
                    <Button
                      variant="outlined"
                      style={{
                        fontWeight: "bolder",
                        color: "black",
                        borderColor: "black",
                        fontSize: 30,
                        height: 50,
                      }}
                      onClick={() => delQty(product)}
                    >
                      -
                    </Button>
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
      <Footer />
    </div>
  );
}

export default Cart;
