import { Divider } from "@mui/material";
import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import { Link, useLocation } from "react-router-dom";
import { Button } from "@mui/material";

function Checkout() {
  const state = useSelector((state) => state.cart);
  const [totalPrice, setTotalPrice] = useState(0)

  useEffect(() => {
    setTotalPrice(JSON.parse(localStorage.getItem('total')))
  }, []);

  return (
    <div className="checkout-container">
      <div className="heading cart-heading">
        <h2>Checkout</h2>
        <h2>Total Price: &nbsp;  ${Math.round(totalPrice)}</h2>
        <Link to='/checkout' state={{totalPrice}}>
        <Button className="checkout-button" variant="outlined"
          style={{
            fontWeight: "bolder",
            color: "white",
            borderColor: "white",
            fontSize: 20,
            height: 50,
            backgroundColor: "#212529",
            borderRadius: 10,
          }}
        >
         Pay Now
        </Button>
          </Link>
      </div>
      <Divider />
      <div className="checkout">
        <div className="checkout-headings">
          <h3 className="checkout-heading">Items</h3>
          <h3 className="checkout-heading">Name</h3>
          <h3 className="checkout-heading">Quantity</h3>
          <h3 className="checkout-heading">Price</h3>
        </div>
        <Divider />
        <div className="checkout-items">
          <div className="checkout-item">
            {state.map((product) => {
              const { image, qty, price, title} = product;
              return (
                <>
                <div className="checkout-item-container">
                  <img className="checkout-item-img" src={image}></img>
                  <h4 style={{textAlign: 'left', justifyContent: 'flex-start', alignItems: 'flex-start', justifySelf: 'flex-start', alignSelf: 'lefts'}}>{title}</h4>
                  <h3>{qty}</h3>
                  <h3>${price * qty}</h3>
                  </div>
                  <Divider />
                  </>
              )
            })}
          </div>
        </div>
        <Divider />
        <div className="total">
          <h1>Total Price: </h1>
          <h2>$&nbsp;{Math.round(totalPrice)}</h2>
        </div>
      </div>
    </div>
  );
}

export default Checkout;
