import React from "react";
import { useLocation } from "react-router-dom";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import "../App.css";
import { Divider } from "@mui/material";
import { Rating } from "@mui/material";
import { Link } from "react-router-dom";
import { addProduct } from "../redux/actions";
import { useDispatch } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";
import { useSelector } from "react-redux";

function Product() {
  const state = useSelector((state) => state.cart);
  const location = useLocation();
  const { product } = location.state;
  // const [cartProducts, setCartProducts] = useState([])

  const dispatch = useDispatch();

  const addProductToCart = (product) => {
      dispatch(addProduct(product))
      alert('Product Added To Cart Successfully!')
      // setCartProducts([...cartProducts, product])
      localStorage.setItem('products', JSON.stringify([...state.splice(index, 1), product]))
      let newVal = state.length + 1
      localStorage.setItem('productsLength', JSON.stringify(newVal))
      // console.log(cartProducts)
  }

  return (
    <>
      <div className="heading">
        <h2>Product Details</h2>
      </div>
      <Divider />
      <div className="product">
        <div className="product-image-container">
          <img className="product-image" src={product.image} alt={'Product'}></img>
        </div>
        <div>
          <Card
            sx={{ backgroundColor: "whitesmoke" }}
            className="product-details"
          >
            <CardContent>
              <Typography variant="h4" component="div">
                {product.title}
              </Typography>
              <Typography
                sx={{ fontSize: 18 }}
                color="text.secondary"
                gutterBottom
              >
                {product.description}
              </Typography>
              <Typography sx={{ mb: 1.9 }} color="text.secondary">
                Category: {product.category}
              </Typography>
              <Typography style={{ fontSize: 28 }}>
                Price: ${product.price}
                <br />
                Rating: {product.rating.rate}
                <Rating
                  name="simple-controlled"
                  value={product.rating.rate}
                  style={{ marginLeft: 10 }}
                />
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="large" style={{ fontWeight: "bolder" }} onClick={() => addProductToCart(product)}>
                Add To Cart
              </Button>
              <Link to='/cart'>
              <Button size="large" style={{ fontWeight: "bolder" }}>
                Go to Cart
              </Button>
                  </Link>
              <Link to='/'>
              <Button size="large" style={{ fontWeight: "bolder" }}>
                Go Back
              </Button>
                  </Link>
            </CardActions>
          </Card>
        </div>
      </div>
    </>
  );
}

export default Product;
